mapper.select:
SELECT Pid
FROM cart_items
WHERE Cart_Id = #{cartId}

SELECT quantity
FROM cart_items
WHERE Pid = #{productId} AND Cart_Id = #{cartId}

SELECT id
FROM cart_items
WHERE Pid = #{productId} AND Cart_id = #{cartId}

mapper.update:
UPDATE cart_items
SET quantity = #{sumQuantity}
WHERE id = #{cartItemId}

UPDATE cart_items
SET quantity = #{amountValue}
WHERE Cart_Id = #{cartId} AND Pid = #{productId}
