mapper.select:
SELECT COUNT(*)
FROM product p



            AND (p.name LIKE CONCAT('%', #{criteria.keyword}, '%'))


            AND p.majorCategory = #{criteria.majorCategory}


            AND p.middleCategory = #{criteria.middleCategory}

SELECT p.id, p.name, p.price, p.amount, p.saleAmount, p.discount, p.majorCategory, p.middleCategory, p.regDate, p.updateDate, p.mainImgPath, p.mainImgName, COUNT(r.id) AS reviewCount, AVG(r.rating) AS averageRating FROM product p LEFT JOIN productreviewboard r ON p.id = r.Pid
AND (p.name LIKE CONCAT('%', #{criteria.keyword}, '%'))
AND p.majorCategory = #{criteria.majorCategory}
AND p.middleCategory = #{criteria.middleCategory}
GROUP BY p.id ORDER BY
p.price DESC ,p.saleAmount DESC, averageRating DESC
p.price ASC ,averageRating DESC, p.saleAmount DESC
p.saleAmount DESC, averageRating DESC
LIMIT #{offset}, #{criteria.amount}
