mapper.select:
SELECT prb.id, prb.Uid, prb.Pid, p.name AS productName, prb.title, prb.content, prb.imgPath, prb.imgName, prb.regDate, prb.updateDate, prb.rating FROM product_review_board prb JOIN product p ON prb.Pid = p.id
AND (prb.title LIKE CONCAT('%', #{criteria.keyword}, '%'))
AND prb.Pid = #{pId}
AND prb.Pid IN (SELECT p.id FROM Product p WHERE p.name LIKE CONCAT('%', #{productName}, '%'))
AND prb.Pid = #{pId}
GROUP BY prb.id ORDER BY prb.updateDate DESC, prb.regDate DESC LIMIT #{offset}, #{criteria.amount}
